package basicprogram;

public class BitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Bitwise AND
		int x=8,y=5;
		int z=x&y;
		System.out.println(z);
		x=10;y=6;
		 z=x&y;
		System.out.println(z);
		//Bitwise OR
		x=12;y=8;
		z=x|y;
		System.out.println(z);
		//Complement
		x=8;
		z=~x;
		System.out.println(z);
		

	}

}
